import React, { useEffect } from 'react';
import mapboxgl from 'mapbox-gl';

mapboxgl.accessToken = 'your-mapbox-access-token'; // Replace with your Mapbox access token

const HistoryMap = () => {
  useEffect(() => {
    const map = new mapboxgl.Map({
      container: 'map', // The id of the div where the map will be rendered
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [-74.5, 40], // Longitude, Latitude for the center of the map
      zoom: 3,
    });

    // Example: Add a marker for an event
    new mapboxgl.Marker()
      .setLngLat([-74.5, 40])
      .setPopup(new mapboxgl.Popup().setHTML('<h3>Historical Event</h3><p>Some event description</p>'))
      .addTo(map);
  }, []);

  return <div id="map" style={{ height: '400px' }}></div>;
};

export default HistoryMap;
