import React, { useEffect } from 'react'; // Import React and useEffect
import { io } from 'socket.io-client'; // Import Socket.IO client
import AQuestionCard from '@/components/aQuestionCard'; // Import AQuestionCard component
import BProgressChart from '@/components/bProgressChart'; // Import BProgressChart component
import HintButton from '@/components/HintButton'; // Import HintButton component
import NextButton from '@/components/NextButton'; // Import NextButton component
import { questions } from '@/utils/questions'; // Import questions
import Card from '@/components/ui/card'; // Import Card component
import CardContent from '@/components/ui/cardContent'; // Import CardContent component
import Button from '@/components/ui/Button'; // Import Button component (only once)
import Progress from '@/components/ui/progress'; // Import Progress component



const HistoryPuzzleGame = () => {
  const [hint, setHint] = useState("");
  const [questionIndex, setQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [progress, setProgress] = useState([0]);

  const handleHint = () => {
    setHint(questions[questionIndex].hint);
  };

  const handleAnswer = (option) => {
    if (option === questions[questionIndex].answer) {
      setScore(score + 10);
      setProgress([...progress, score + 10]);
    }
    handleNext();
  };

  const handleNext = () => {
    setHint("");
    setQuestionIndex((prev) => (prev + 1) % questions.length);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-4">
      <motion.div initial={{ opacity: 0, y: -20 }} animate={{ opacity: 1, y: 0 }}>
        <QuestionCard
          question={questions[questionIndex].question}
          options={questions[questionIndex].options}
          handleAnswer={handleAnswer}
        />
        {hint && <p className="mt-4 text-gray-600 text-sm">ðŸ’¡ Hint: {hint}</p>}
        <div className="flex justify-between items-center mt-6">
          <HintButton handleHint={handleHint} />
          <NextButton handleNext={handleNext} />
        </div>
      </motion.div>
      <ProgressChart progress={progress} />
    </div>
  );
};

export default HistoryPuzzleGame;
