import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Game = () => {
  const [question, setQuestion] = useState('');
  const [userAnswer, setUserAnswer] = useState('');
  const [isCorrect, setIsCorrect] = useState(null);
  const [era, setEra] = useState('Ancient Rome'); // Default era

  // Fetch a new question based on the current era
  useEffect(() => {
    fetchQuestion();
  }, [era]);

  const fetchQuestion = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/getQuestion', {
        params: { era },
      });
      setQuestion(response.data.question);
    } catch (error) {
      console.error('Error fetching question:', error);
    }
  };

  const handleAnswer = async () => {
    const correctAnswer = 'Correct answer'; // Placeholder
    const isAnswerCorrect = userAnswer.toLowerCase() === correctAnswer.toLowerCase();
    setIsCorrect(isAnswerCorrect);

    // Save user progress to backend
    await axios.post('http://localhost:5000/api/saveProgress', {
      username: 'player1', // Example username
      question,
      userAnswer,
      isCorrect: isAnswerCorrect,
    });

    // Fetch the next question after a brief delay
    setTimeout(() => {
      fetchQuestion();
      setUserAnswer('');
      setIsCorrect(null);
    }, 2000);
  };

  return (
    <div className="game-container">
      <h2>Current Era: {era}</h2>
      <div className="question-container">
        <p>{question}</p>
        <input
          type="text"
          value={userAnswer}
          onChange={(e) => setUserAnswer(e.target.value)}
          placeholder="Your Answer"
        />
        <button onClick={handleAnswer}>Submit Answer</button>
      </div>
      {isCorrect !== null && (
        <p>{isCorrect ? 'Correct!' : 'Incorrect. Try again!'}</p>
      )}
    </div>
  );
};

export default Game;
