import React, { useState } from 'react';
import MainMenu from './components/MainMenu';
import SubjectSelection from './components/SubjectSelection';
import Game from './components/Game';
import Results from './components/Results';
import ProgressTracker from './components/ProgressTracker';

const App = () => {
    const [currentStep, setCurrentStep] = useState('menu');
    const [selectedSubject, setSelectedSubject] = useState(null);
    const [score, setScore] = useState(0);
    const [questionsAnswered, setQuestionsAnswered] = useState(0);
    const [progress, setProgress] = useState({ correct: 0, total: 0 });

    const handleStart = () => {
        setCurrentStep('subjectSelection');
    };

    const handleSelectSubject = (subject) => {
        setSelectedSubject(subject);
        setCurrentStep('game');
    };

    const handleGameComplete = (score, questionsAnswered) => {
        setScore(score);
        setQuestionsAnswered(questionsAnswered);
        setProgress((prev) => ({
            correct: prev.correct + score,
            total: prev.total + questionsAnswered,
        }));
        setCurrentStep('results');
    };

    const renderStep = () => {
        switch (currentStep) {
            case 'menu':
                return <MainMenu onStart={handleStart} />;
            case 'subjectSelection':
                return <SubjectSelection onSelect={handleSelectSubject} />;
            case 'game':
                return <Game subject={selectedSubject} onComplete={handleGameComplete} />;
            case 'results':
                return <Results score={score} total={questionsAnswered} />;
            default:
                return null;
        }
    };

    return (
        <div>
            <ProgressTracker progress={progress} />
            {renderStep()}
        </div>
    );
};

export default App;
